import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Star, Calendar, Clock, Plus, Gamepad2, Filter, ChevronLeft, ChevronRight, Grid, List } from 'lucide-react';
import './App.css';

const API_URL = 'http://localhost:5001';

function App() {
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]); // Store unfiltered games
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [view, setView] = useState('browse');
  const [page, setPage] = useState(1);
  const [sortBy, setSortBy] = useState('rating');
  const [viewMode, setViewMode] = useState('grid'); // grid or list

  // Recommendation filters
  const [filters, setFilters] = useState({
    mood: '',
    timeCommitment: '',
    platform: '',
    genre: ''
  });
  const hasActiveFilters = Object.values(filters).some(v => v !== '');
  const gamesPerPage = 40;

  useEffect(() => {
    fetchGames();
  }, [page, sortBy]);

const fetchGames = async () => {
  setLoading(true);
  try {
    const response = await axios.get(`${API_URL}/api/games?page=${page}&limit=40`);
    const gamesData = response.data.results || [];
    setAllGames(gamesData);
    setGames(gamesData);  // Always set both
    console.log(`Loaded ${gamesData.length} games from total of ${response.data.total}`);
  } catch (error) {
    console.error('Failed to fetch games:', error);
  }
  setLoading(false);
};

  const searchGames = async (query) => {
    if (!query) {
      setGames(allGames);
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/api/games/search?q=${query}`);
      setGames(response.data.results || []);
    } catch (error) {
      console.error('Search failed:', error);
    }
    setLoading(false);
  };

  useEffect(() => {
    const delaySearch = setTimeout(() => {
      if (searchTerm) {
        searchGames(searchTerm);
      } else {
        setGames(allGames);
      }
    }, 500);

    return () => clearTimeout(delaySearch);
  }, [searchTerm]);

  // Filter games based on selected criteria
useEffect(() => {
  if (!allGames.length) return; // Don't filter if no games loaded yet

  let filtered = [...allGames];

  if (filters.mood) {
    const moodMap = {
      'relaxing': ['Puzzle', 'Simulation', 'Casual', 'Indie'],
      'intense': ['Action', 'Shooter', 'Fighting'],
      'story': ['RPG', 'Adventure'],
      'competitive': ['Sports', 'Racing', 'Fighting']
    };
    const targetGenres = moodMap[filters.mood] || [];
    filtered = filtered.filter(game => 
      game.genres && Array.isArray(game.genres) && 
      game.genres.some(g => targetGenres.includes(g))
    );
  }

  if (filters.timeCommitment) {
    filtered = filtered.filter(game => {
      const playtime = game.playtime || 0;
      switch(filters.timeCommitment) {
        case 'quick': return playtime <= 5;
        case 'medium': return playtime > 5 && playtime <= 20;
        case 'long': return playtime > 20 && playtime <= 50;
        case 'endless': return playtime > 50;
        default: return true;
      }
    });
  }

  if (filters.platform) {
    filtered = filtered.filter(game => 
      game.platforms && Array.isArray(game.platforms) && 
      game.platforms.includes(filters.platform)
    );
  }

  if (filters.genre) {
    filtered = filtered.filter(game => 
      game.genres && Array.isArray(game.genres) && 
      game.genres.includes(filters.genre)
    );
  }

  // Apply sorting
  filtered.sort((a, b) => {
    switch(sortBy) {
      case 'rating': return (b.rating || 0) - (a.rating || 0);
      case 'recent': return new Date(b.released || 0) - new Date(a.released || 0);
      case 'name': return (a.title || '').localeCompare(b.title || '');
      default: return 0;
    }
  });

  setGames(filtered);
}, [filters, sortBy]); // Removed allGames from dependencies

  const resetFilters = () => {
    setFilters({
      mood: '',
      timeCommitment: '',
      platform: '',
      genre: ''
    });
    setGames(allGames);
  };



  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <div className="container">
          <div className="logo">
            <Gamepad2 size={32} />
            <h1>playtraq</h1>
          </div>

          <div className="search-bar">
            <Search size={20} />
            <input
              type="text"
              placeholder="Search games..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <nav className="nav">
            <button 
              className={view === 'browse' ? 'active' : ''} 
              onClick={() => setView('browse')}
            >
              Browse
            </button>
            <button 
              className={view === 'library' ? 'active' : ''} 
              onClick={() => setView('library')}
            >
              My Library
            </button>
            <button className="nav-profile">
              <div className="avatar-placeholder" />
            </button>
          </nav>
        </div>
      </header>

      {/* Filters Bar */}
      <div className="filters-bar">
        <div className="container">
          <div className="filters-row">
            <select 
              value={filters.mood} 
              onChange={(e) => setFilters({...filters, mood: e.target.value})}
              className="filter-select"
            >
              <option value="">Any Mood</option>
              <option value="relaxing">Relaxing</option>
              <option value="intense">Intense</option>
              <option value="story">Story-Rich</option>
              <option value="competitive">Competitive</option>
            </select>

            <select 
              value={filters.timeCommitment} 
              onChange={(e) => setFilters({...filters, timeCommitment: e.target.value})}
              className="filter-select"
            >
              <option value="">Any Length</option>
              <option value="quick">Quick (under 5 hrs)</option>
              <option value="medium">Medium (5-20 hrs)</option>
              <option value="long">Long (20-50 hrs)</option>
              <option value="endless">Endless (50+ hrs)</option>
            </select>

            <select 
              value={filters.platform} 
              onChange={(e) => setFilters({...filters, platform: e.target.value})}
              className="filter-select"
            >
              <option value="">All Platforms</option>
              <option value="PC">PC</option>
              <option value="PlayStation 5">PlayStation 5</option>
              <option value="Xbox Series S/X">Xbox Series</option>
              <option value="Nintendo Switch">Switch</option>
            </select>

            <select 
              value={filters.genre} 
              onChange={(e) => setFilters({...filters, genre: e.target.value})}
              className="filter-select"
            >
              <option value="">All Genres</option>
              <option value="Action">Action</option>
              <option value="Adventure">Adventure</option>
              <option value="RPG">RPG</option>
              <option value="Strategy">Strategy</option>
              <option value="Shooter">Shooter</option>
              <option value="Puzzle">Puzzle</option>
              <option value="Platformer">Platformer</option>
              <option value="Racing">Racing</option>
              <option value="Sports">Sports</option>
              <option value="Simulation">Simulation</option>
            </select>

            {hasActiveFilters && (
              <button onClick={resetFilters} className="reset-filters-btn">
                Reset Filters
              </button>
            )}
          </div>

          <div className="sort-controls">
            <select 
              value={sortBy} 
              onChange={(e) => setSortBy(e.target.value)}
              className="sort-select"
            >
              <option value="rating">Top Rated</option>
              <option value="recent">Most Recent</option>
              <option value="name">Alphabetical</option>
            </select>

            <div className="view-toggles">
              <button 
                className={viewMode === 'grid' ? 'active' : ''}
                onClick={() => setViewMode('grid')}
              >
                <Grid size={18} />
              </button>
              <button 
                className={viewMode === 'list' ? 'active' : ''}
                onClick={() => setViewMode('list')}
              >
                <List size={18} />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <main className="main-content">
        <div className="container">
          {view === 'browse' && (
            <>
              {loading ? (
                <div className="loading-state">
                  <p>Loading games...</p>
                </div>
              ) : (
                <>
                  <div className="results-header">
                    <h2>
                      {hasActiveFilters ? 'Filtered Results' : 'All Games'}
                      <span className="results-count"> ({games.length} games)</span>
                    </h2>
                  </div>

                  <div className={viewMode === 'grid' ? 'games-grid' : 'games-list'}>
                    {games.slice(0, gamesPerPage).map(game => (
                      <div key={game.id} className={viewMode === 'grid' ? 'game-card' : 'game-card-list'}>
                        <div className="game-cover">
                          <img src={game.coverImage} alt={game.title} />
                          <div className="game-overlay">
                            <button className="add-btn">
                              <Plus size={20} />
                              Add to Library
                            </button>
                          </div>
                        </div>
                        <div className="game-info">
                          <h3>{game.title}</h3>
                          <div className="game-meta">
                            <span className="year">
                              <Calendar size={14} />
                              {game.releaseYear}
                            </span>
                            <span className="rating">
                              <Star size={14} />
                              {game.rating?.toFixed(1) || 'N/A'}
                            </span>
                            {game.playtime && (
                              <span className="playtime">
                                <Clock size={14} />
                                {game.playtime}h
                              </span>
                            )}
                          </div>
                          <div className="game-genres">
                            {game.genres?.slice(0, 3).map(g => (
                              <span key={g} className="genre-tag">{g}</span>
                            ))}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Pagination */}
                  <div className="pagination">
                    <button 
                      onClick={() => setPage(Math.max(1, page - 1))}
                      disabled={page === 1}
                      className="page-btn"
                    >
                      <ChevronLeft size={20} />
                      Previous
                    </button>
                    <span className="page-info">Page {page}</span>
                    <button 
                      onClick={() => setPage(page + 1)}
                      disabled={games.length < gamesPerPage}
                      className="page-btn"
                    >
                      Next
                      <ChevronRight size={20} />
                    </button>
                  </div>
                </>
              )}
            </>
          )}

          {view === 'library' && (
            <div className="library-view">
              <h2>My Game Library</h2>
              <div className="library-stats">
                <div className="stat-card">
                  <h4>Currently Playing</h4>
                  <p className="stat-number">0</p>
                </div>
                <div className="stat-card">
                  <h4>Completed</h4>
                  <p className="stat-number">0</p>
                </div>
                <div className="stat-card">
                  <h4>Want to Play</h4>
                  <p className="stat-number">0</p>
                </div>
              </div>
              <p className="empty-state">Add games to build your library!</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
