import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Star, Calendar, Clock, Plus, Gamepad2, TrendingUp, Filter, Sparkles } from 'lucide-react';
import RecommendationFlow from './RecommendationFlow';
import './App.css';

const API_URL = 'http://localhost:5001';

function App() {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [view, setView] = useState('browse'); // browse, library
  const [showRecommendationFlow, setShowRecommendationFlow] = useState(false);

  // Fetch real games from API
  useEffect(() => {
    fetchGames();
  }, []);

  const fetchGames = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/api/games`);
      console.log('Games loaded:', response.data);
      setGames(response.data.results || []);
    } catch (error) {
      console.error('Failed to fetch games:', error);
      // Fallback to mock data if API fails
      setGames([
        {
          id: '1',
          title: 'The Legend of Zelda: Breath of the Wild',
          releaseYear: 2017,
          genres: ['Adventure', 'Action'],
          platforms: ['Nintendo Switch'],
          coverImage: 'https://via.placeholder.com/200x300',
          rating: 4.8
        }
      ]);
    }
    setLoading(false);
  };

  const searchGames = async (query) => {
    if (!query) {
      fetchGames();
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/api/games/search?q=${query}`);
      setGames(response.data.results || []);
    } catch (error) {
      console.error('Search failed:', error);
    }
    setLoading(false);
  };

  // Debounce search
  useEffect(() => {
    const delaySearch = setTimeout(() => {
      if (searchTerm) {
        searchGames(searchTerm);
      } else {
        fetchGames();
      }
    }, 500);

    return () => clearTimeout(delaySearch);
  }, [searchTerm]);

  const handleRecommendations = (recs) => {
    setRecommendations(recs);
    setShowRecommendationFlow(false);
  };

  const filteredGames = games;

  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <div className="container">
          <div className="logo">
            <Gamepad2 size={32} />
            <h1>playtraq</h1>
          </div>

          <nav className="nav">
            <button 
              className={view === 'browse' ? 'active' : ''} 
              onClick={() => setView('browse')}
            >
              Browse
            </button>
            <button 
              className={view === 'library' ? 'active' : ''} 
              onClick={() => setView('library')}
            >
              My Library
            </button>
          </nav>

          <div className="search-bar">
            <Search size={20} />
            <input
              type="text"
              placeholder="Search games..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="main-content">
        <div className="container">
          {view === 'browse' && (
            <>
              {/* Recommendation Section - Always visible at top */}
              <div className="recommendation-section">
                {!showRecommendationFlow ? (
                  <div className="recommendation-prompt">
                    <div className="prompt-content">
                      <Sparkles size={24} />
                      <div>
                        <h2>Find Your Perfect Game</h2>
                        <p>Tell us what you're looking for and get personalized recommendations</p>
                      </div>
                      <button 
                        onClick={() => setShowRecommendationFlow(true)} 
                        className="start-flow-btn"
                      >
                        Get Recommendations →
                      </button>
                    </div>

                    {/* Show recommendations if we have them */}
                    {recommendations.length > 0 && (
                      <div className="inline-recommendations">
                        <h3>Your Recommendations:</h3>
                        <div className="rec-cards-inline">
                          {recommendations.slice(0, 3).map((game, index) => (
                            <div key={index} className="rec-card-inline">
                              <h4>{game.title}</h4>
                              <p>{game.reason}</p>
                              {game.matchScore && (
                                <span className="match-badge">{game.matchScore}% Match</span>
                              )}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="recommendation-flow-container">
                    <button 
                      onClick={() => setShowRecommendationFlow(false)}
                      className="close-flow-btn"
                    >
                      ← Back to Browse
                    </button>
                    <RecommendationFlow onRecommendations={handleRecommendations} />
                  </div>
                )}
              </div>

              {/* Games Grid - Only show when not in recommendation flow */}
              {!showRecommendationFlow && (
                <>
                  <h2 style={{ marginTop: '3rem', marginBottom: '2rem' }}>Popular Games</h2>
                  {loading ? (
                    <div style={{ textAlign: 'center', padding: '2rem' }}>
                      <p>Loading games...</p>
                    </div>
                  ) : (
                    <div className="games-grid" style={{ 
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
                      gap: '2rem',
                      marginBottom: '3rem'
                    }}>
                      {filteredGames.map(game => (
                        <div key={game.id} className="game-card" style={{ background: 'rgba(255, 255, 255, 0.05)' }}>
                          <div className="game-cover">
                            <img src={game.coverImage} alt={game.title} />
                            <div className="game-overlay">
                              <button className="add-btn">
                                <Plus size={20} />
                                Add to Library
                              </button>
                            </div>
                          </div>
                          <div className="game-info" style={{ padding: '1rem' }}>
                            <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem' }}>{game.title}</h3>
                            <div className="game-meta" style={{ marginBottom: '0.5rem' }}>
                              <span className="year">
                                <Calendar size={14} />
                                {game.releaseYear}
                              </span>
                              <span className="rating">
                                <Star size={14} />
                                {game.rating}
                              </span>
                            </div>
                            <div className="game-genres">
                              {game.genres && game.genres.map(g => (
                                <span key={g} className="genre-tag">{g}</span>
                              ))}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </>
              )}
            </>
          )}

          {view === 'library' && (
            <div className="library-view">
              <h2>My Game Library</h2>
