import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Star, Calendar, Clock, Plus, Gamepad2, TrendingUp, Filter } from 'lucide-react';
import RecommendationFlow from './RecommendationFlow';
import './App.css';

const API_URL = 'http://localhost:5001';

function App() {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [view, setView] = useState('browse'); // browse, recommendations, library
  const [showRecommendationFlow, setShowRecommendationFlow] = useState(false);

  // Fetch real games from API
  useEffect(() => {
    fetchGames();
  }, []);

  const fetchGames = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/api/games`);
      console.log('Games loaded:', response.data);
      setGames(response.data.results || []);
    } catch (error) {
      console.error('Failed to fetch games:', error);
      // Fallback to mock data if API fails
      setGames([
        {
          id: '1',
          title: 'The Legend of Zelda: Breath of the Wild',
          releaseYear: 2017,
          genres: ['Adventure', 'Action'],
          platforms: ['Nintendo Switch'],
          coverImage: 'https://via.placeholder.com/200x300',
          rating: 4.8
        }
      ]);
    }
    setLoading(false);
  };

  const searchGames = async (query) => {
    if (!query) {
      fetchGames();
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/api/games/search?q=${query}`);
      setGames(response.data.results || []);
    } catch (error) {
      console.error('Search failed:', error);
    }
    setLoading(false);
  };

  // Debounce search
  useEffect(() => {
    const delaySearch = setTimeout(() => {
      if (searchTerm) {
        searchGames(searchTerm);
      } else {
        fetchGames();
      }
    }, 500);

    return () => clearTimeout(delaySearch);
  }, [searchTerm]);

  const handleRecommendations = (recs) => {
    setRecommendations(recs);
    setShowRecommendationFlow(false);
    setView('recommendations');
  };

  const filteredGames = games;

  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <div className="container">
          <div className="logo">
            <Gamepad2 size={32} />
            <h1>playtraq</h1>
          </div>

          <nav className="nav">
            <button 
              className={view === 'browse' ? 'active' : ''} 
              onClick={() => setView('browse')}
            >
              Browse
            </button>
            <button 
              className={view === 'recommendations' ? 'active' : ''} 
              onClick={() => setView('recommendations')}
            >
              AI Recommendations
            </button>
            <button 
              className={view === 'library' ? 'active' : ''} 
              onClick={() => setView('library')}
            >
              My Library
            </button>
          </nav>

          <div className="search-bar">
            <Search size={20} />
            <input
              type="text"
              placeholder="Search games..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </header>

      {/* NEW: Smart Recommendation Bar - Simplified */}
      {view === 'browse' && !showRecommendationFlow && (
        <div className="recommendation-bar">
          <div className="container">
            <h3>ðŸŽ® Get Personalized Game Recommendations</h3>
            <p style={{ marginBottom: '1rem', color: 'rgba(255,255,255,0.8)' }}>
              Answer a few questions and we'll find your perfect game based on your mood, playtime, and preferences
            </p>
            <button 
              onClick={() => setShowRecommendationFlow(true)} 
              className="recommend-btn"
              style={{ fontSize: '1.1rem', padding: '1rem 2rem' }}
            >
              ðŸš€ Start Game Finder
            </button>
          </div>
        </div>
      )}

      {/* NEW: Show Recommendation Flow as Modal */}
      {showRecommendationFlow && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.95)',
          zIndex: 1000,
          overflow: 'auto',
          padding: '2rem'
        }}>
          <button 
            onClick={() => setShowRecommendationFlow(false)}
            style={{
              position: 'absolute',
              top: '2rem',
              right: '2rem',
              background: 'rgba(255,255,255,0.1)',
              border: 'none',
              color: 'white',
              padding: '0.5rem 1rem',
              borderRadius: '50px',
              cursor: 'pointer',
              fontSize: '1.2rem'
            }}
          >
            âœ•
          </button>
          <RecommendationFlow onRecommendations={handleRecommendations} />
        </div>
      )}

      {/* Main Content */}
      <main className="main-content">
        <div className="container">
          {view === 'browse' && (
            <>
              {loading ? (
                <div style={{ textAlign: 'center', padding: '2rem' }}>
                  <p>Loading games...</p>
                </div>
              ) : (
                <>
                  <h2>Popular Games</h2>
                  <div className="games-grid">
                    {filteredGames.map(game => (
                      <div key={game.id} className="game-card">
                        <div className="game-cover">
                          <img src={game.coverImage} alt={game.title} />
                          <div className="game-overlay">
                            <button className="add-btn">
                              <Plus size={20} />
                              Add to Library
                            </button>
                          </div>
                        </div>
                        <div className="game-info">
                          <h3>{game.title}</h3>
                          <div className="game-meta">
                            <span className="year">
                              <Calendar size={14} />
                              {game.releaseYear}
                            </span>
                            <span className="rating">
                              <Star size={14} />
                              {game.rating}
                            </span>
                          </div>
                          <div className="game-genres">
                            {game.genres && game.genres.map(g => (
                              <span key={g} className="genre-tag">{g}</span>
                            ))}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </>
              )}
            </>
          )}

          {view === 'recommendations' && (
            <div className="recommendations-view">
              <h2>ðŸ¤– Your Personalized Recommendations</h2>
              {recommendations.length > 0 ? (
                <div className="recommendations-list">
                  {recommendations.map((game, index) => (
                    <div key={index} className="recommendation-card">
                      <h3>{game.title}</h3>
                      <p className="recommendation-reason">{game.reason}</p>
                      <span className="year">{game.releaseYear || 'Coming Soon'}</span>
                      {game.matchScore && (
                        <div style={{ marginTop: '0.5rem' }}>
                          <span style={{ 
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                            padding: '0.25rem 0.75rem',
                            borderRadius: '20px',
                            fontSize: '0.9rem'
                          }}>
                            {game.matchScore}% Match
                          </span>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <div style={{ textAlign: 'center', padding: '3rem' }}>
                  <p>No recommendations yet!</p>
                  <button 
                    onClick={() => {
                      setView('browse');
                      setShowRecommendationFlow(true);
                    }}
                    className="recommend-btn"
                    style={{ marginTop: '1rem' }}
                  >
                    Get Recommendations
                  </button>
                </div>
              )}
            </div>
          )}

          {view === 'library' && (
            <div className="library-view">
              <h2>My Game Library</h2>
              <div className="library-stats">
                <div className="stat-card">
                  <h4>Currently Playing</h4>
                  <p className="stat-number">3</p>
                </div>
                <div className="stat-card">
                  <h4>Completed</h4>
                  <p className="stat-number">24</p>
                </div>
                <div className="stat-card">
                  <h4>Want to Play</h4>
                  <p className="stat-number">15</p>
                </div>
              </div>
              <p className="empty-state">Add games to build your library!</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
